","keccak256":"0x9f4357008a8f7d8c8bf5d48902e789637538d8c016be5766610901b4bba81514","license":"MIT"},"src/BaseCheckin.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

import { ICheckin } from "./interfaces/ICheckin.sol";
import { TransferFromHelper } from "./utils/transfers/TransferFromHelper.sol";
import { Initializable } from "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import { AccessControlEnumerable } from "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import { Math } from "@openzeppelin/contracts/utils/math/Math.sol";

abstract contract BaseCheckin is Initializable, AccessControlEnumerable, ICheckin {
  using TransferFromHelper for address;
  /**
   * @notice The streak lifetime in second(s).
   * @dev This variable will NOT be updated after initializing because of affecting to all current active streaks.
   */

  uint256 internal _initialTime;
  uint256 internal _streakLifeTime;
  mapping(address => Streak) internal _usersStreakMap;
  /**
   * @notice The reset duration in second(s).
   * @dev This reset duration is calculated based on the UTC.
   */
  uint256 internal _resetTime;

  /**
   * @dev Defines the duration in which lost streaks can be restored.
   * Users have the opportunity to restore lost streaks during this freeze period after the day they were lost.
   */
  uint256 internal _freezeTime;

  /// @dev The address for receiving restoration cost.
  address internal _treasury;
  /// @dev The address that paid to restore the lost streak.
  address internal _paymentToken;

  /**
   * @dev Store array of cost for restoration base on the lower bound of lost streak ranges.
   * The cost is pegged in `_paymentToken`.
   *
   * The below example table describes the cost to restore the lost streak.
   *
   * |  lost streak range  |  lower bound  |  fee in Token |
   * | ------------------- | ------------- | ------------- |
   * | 01 - 10             | 01            | 1             |
   * | 11 - 20             | 11            | 3             |
   * | 21 - 50             | 21            | 6             |
   * | 51 - 99             | 51            | 10            |
   * | 100+                | 100           | 15            |
   */
  RestorationCost[] internal _restorationCosts;

  constructor() {
    _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
  }

  function initialize(
    uint256 streakLifeTime,
    uint256 resetTime,
    uint256 freezeTime,
    address paymentToken,
    address treasury,
    RestorationCost[] calldata restorationCosts
  ) external onlyRole(DEFAULT_ADMIN_ROLE) initializer {
    _setStreakLifeTime(streakLifeTime);
    _setResetDuration(resetTime);
    _setFreezePeriod(freezeTime);
    _setPaymentToken(paymentToken);
    _setTreasury(treasury);
    _setRestorationCosts(restorationCosts);
    _setInitialTime();
  }

  /**
   * @dev Activate the streak for address `to`.
   * @param to Address for activating streak.
   *
   * Requirements:
   * - Revert when activating before the allowed activation block.
   * - Revert when streak is still active.
   * - If caller is different from address to, caller must be delegated by address `to`.
   *
   * Emits the {StreakUpdated} event.
   */
  function _activateStreak(address to) internal {
    Streak memory streak = _usersStreakMap[to];
    if (_checkNewUser(streak)) {
      _unsafeCheckin({ streakCount: streak.count, to: to, isLostStreak: false });
      return;
    }
    (uint256 currentDayIndex, uint256 lastCheckedDayIndex) = _calculateElapsedDays(streak);
    (bool isLostStreak, bool hasCheckedToday) = _getActivationStatus(currentDayIndex, lastCheckedDayIndex);
    bool freezeTimeEnded = _isFreezeTimeEnded(lastCheckedDayIndex);
    if (hasCheckedToday) revert ErrAlreadyActivatedStreak();
    if (to != msg.sender && isLostStreak && !freezeTimeEnded) {
      revert ErrStartNewStreakByDelegatee();
    }

    _unsafeCheckin({ streakCount: streak.count, to: to, isLostStreak: isLostStreak });
  }

  /**
   * @dev Restore the lost streak.
   * @param to Address for restoring streak.
   * @param maxAmountIn the amount of `paymentToken` that users accept to pay for restoring their streak.
   *
   * Requirements:
   * - Revert when there's no lost streak.
   * - Revert when restore not in freeze period.
   * - If caller is different from address to, caller must be delegated by address `to`.
   *
   * Emits the {StreakRestored} event.
   */
  function _restoreStreak(address to, uint256 maxAmountIn) internal {
    Streak memory streak = _usersStreakMap[to];
    if (_checkNewUser(streak)) {
      revert ErrNewUserCannotRestore();
    }
    uint256 lostStreakCount = streak.count;
    if (lostStreakCount == 0) revert ErrZeroLostStreakCount();

    (uint256 currentDayIndex, uint256 lastCheckedDayIndex) = _calculateElapsedDays(streak);

    (bool isLostStreak,) = _getActivationStatus(currentDayIndex, lastCheckedDayIndex);
    if (!isLostStreak) revert ErrNoLostStreak();

    bool freezeTimeEnded = _isFreezeTimeEnded(lastCheckedDayIndex);
    if (freezeTimeEnded) revert ErrFreezeTimeEnded();

    _unsafeCheckin({ streakCount: lostStreakCount, to: to, isLostStreak: false });

    // charge restoration cost
    uint256 costInPaymentToken =
      _handlePaymentForRestoration({ lostStreakCount: lostStreakCount, maxAmountIn: maxAmountIn });

    emit StreakRestored(to, lostStreakCount, streak.lastActivated, costInPaymentToken);
  }

  /// @inheritdoc ICheckin
  function forceRestoreStreak(address[] calldata addresses, uint256[] calldata streaks)
    external
    onlyRole(DEFAULT_ADMIN_ROLE)
  {
    uint256 length = addresses.length;
    if (length != streaks.length) revert ErrInvalidLength();

    for (uint256 i; i < length; ++i) {
      _unsafeCheckin({ streakCount: streaks[i], to: addresses[i], isLostStreak: false });
    }
  }

  /// @inheritdoc ICheckin
  function isRestorable(address user) external view returns (bool) {
    Streak memory streak = _usersStreakMap[user];
    if (_checkNewUser(streak)) {
      return false;
    }
    (uint256 currentDayIndex, uint256 lastCheckedDayIndex) = _calculateElapsedDays(streak);
    (bool lostStreak,) = _getActivationStatus(currentDayIndex, lastCheckedDayIndex);
    bool freezeTimeEnded = _isFreezeTimeEnded(lastCheckedDayIndex);

    return lostStreak && !freezeTimeEnded;
  }

  /// @inheritdoc ICheckin
  function getStreak(address user)
    external
    view
    override
    returns (uint256 currentStreakCount, uint256 lastActivated, uint256 longestStreakCount, uint256 lostStreakCount)
  {
    Streak memory streak = _usersStreakMap[user];
    if (_checkNewUser(streak)) {
      return (0, 0, 0, 0);
    }
    (uint256 currentDayIndex, uint256 lastCheckedDayIndex) = _calculateElapsedDays(streak);
    (bool isLostStreak,) = _getActivationStatus(currentDayIndex, lastCheckedDayIndex);

    if (isLostStreak) {
      lostStreakCount = streak.count;
    } else {
      currentStreakCount = streak.count;
    }

    lastActivated = streak.lastActivated;
    longestStreakCount = streak.longestCount;
  }

  /// @inheritdoc ICheckin
  function getActivationStatus(address user) external view override returns (bool isLostStreak, bool hasCheckedToday) {
    Streak memory streak = _usersStreakMap[user];
    if (_checkNewUser(streak)) {
      return (false, false);
    }
    (uint256 currentDayIndex, uint256 lastCheckedDayIndex) = _calculateElapsedDays(streak);
    (isLostStreak, hasCheckedToday) = _getActivationStatus(currentDayIndex, lastCheckedDayIndex);
  }

  /// @inheritdoc ICheckin
  function getRestorationCosts(uint256 lostStreakCount) external view returns (uint256 costInPaymentToken) {
    return _getRestorationCosts(lostStreakCount);
  }

  /// @inheritdoc ICheckin
  function getStreakLifeTime() external view returns (uint256) {
    return _streakLifeTime;
  }

  /// @inheritdoc ICheckin
  function getResetDuration() external view returns (uint256) {
    return _resetTime;
  }

  /// @inheritdoc ICheckin
  function getFreezePeriod() external view returns (uint256) {
    return _freezeTime;
  }

  /// @inheritdoc ICheckin
  function getTreasury() external view returns (address) {
    return _treasury;
  }

  /// @inheritdoc ICheckin
  function getPaymentToken() external view returns (address) {
    return _paymentToken;
  }

  /// @inheritdoc ICheckin
  function getAllRestorationCosts() external view returns (RestorationCost[] memory) {
    return _restorationCosts;
  }

  /// @inheritdoc ICheckin
  function getInitialTime() external view returns (uint256) {
    return _initialTime;
  }

  // @inheritdoc ICheckin
  function setResetDuration(uint256 newResetDuration) external onlyRole(DEFAULT_ADMIN_ROLE) {
    _setResetDuration(newResetDuration);
  }

  /// @inheritdoc ICheckin
  function setFreezePeriod(uint256 newFreezePeriod) external onlyRole(DEFAULT_ADMIN_ROLE) {
    _setFreezePeriod(newFreezePeriod);
  }

  /// @inheritdoc ICheckin
  function setPaymentToken(address paymentToken) external onlyRole(DEFAULT_ADMIN_ROLE) {
    _setPaymentToken(paymentToken);
  }

  /// @inheritdoc ICheckin
  function setTreasury(address treasury) external onlyRole(DEFAULT_ADMIN_ROLE) {
    _setTreasury(treasury);
  }

  /// @inheritdoc ICheckin
  function setRestorationCosts(RestorationCost[] memory restorationCosts) external onlyRole(DEFAULT_ADMIN_ROLE) {
    _setRestorationCosts(restorationCosts);
  }

  ///@dev Two arrays must have same length and ascending order.
  function _setRestorationCosts(RestorationCost[] memory restorationCosts) internal {
    delete _restorationCosts;

    uint256 length = restorationCosts.length;
    if (length == 0 || length != restorationCosts.length) revert ErrInvalidLength();

    _restorationCosts.push(restorationCosts[0]);

    for (uint256 i = 1; i < length; ++i) {
      if (restorationCosts[i].lowerMilestone <= restorationCosts[i - 1].lowerMilestone) {
        revert ErrInvalidOrderOfRestorationCosts();
      }

      _restorationCosts.push(restorationCosts[i]);
    }

    emit RestorationCostUpdated(restorationCosts);
  }

  /// @dev Update freeze period in timestamp.
  function _setFreezePeriod(uint256 freezePeriod) internal {
    _freezeTime = freezePeriod;

    emit FreezePeriodUpdated(freezePeriod);
  }

  /// @dev Update payment token address.
  function _setPaymentToken(address paymentToken) internal {
    _paymentToken = paymentToken;

    emit PaymentTokenUpdated(paymentToken);
  }

  /// @dev Update treasury address.
  function _setTreasury(address treasury) internal {
    _treasury = treasury;

    emit TreasuryUpdated(treasury);
  }

  /// @dev Update treasury address.
  function _setInitialTime() internal {
    /// @dev Reset the timestamp to 00:00 GMT of the current block time
    _initialTime = block.timestamp / _streakLifeTime;
    /// @dev Decrease initial Time to 1 day to avoid overflow
    _initialTime -= 1;
    _initialTime *= _streakLifeTime;

    _initialTime += _resetTime;

    emit InitialTimeUpdated(_initialTime);
  }

  /// @dev Determine the cost in USD and calculate amount in `paymentToken`. Then transfer it to treasury.
  function _handlePaymentForRestoration(uint256 lostStreakCount, uint256 maxAmountIn)
    internal
    returns (uint256 costInPaymentToken)
  {
    costInPaymentToken = _getRestorationCosts(lostStreakCount);

    // return immediately if there's no cost.
    if (costInPaymentToken == 0) return costInPaymentToken;

    if (costInPaymentToken > maxAmountIn) {
      revert ErrInsufficientPayment();
    }

    _paymentToken.safeTransferFrom(msg.sender, _treasury, costInPaymentToken);
  }

  /// @dev Return the cost in USD to restore the `lostStreakCount`.
  function _getRestorationCosts(uint256 lostStreakCount) internal view returns (uint256 costInPaymentToken) {
    RestorationCost[] memory restorations = _restorationCosts;
    uint256 length = restorations.length;

    // When there's no restoration cost, immediately return to prevent underflow errors in the below reverse loop.
    if (length == 0) return 0;

    // determine the index range of the lost streak by searching from biggest milestone.
    // if the lost streak larger or equal to any lower milestone, the range is from that milestone to the next one.
    for (uint256 i = length - 1; i >= 0; --i) {
      if (lostStreakCount >= restorations[i].lowerMilestone) {
        costInPaymentToken = uint256(restorations[i].costInToken);
        break;
      }

      // break immediately when index is zero to avoid underflow issue.
      if (i == 0) break;
    }
  }

  /**
   * @dev Return the activation status of `streak`.
   * @return isLostStreak Flag indicates whether streak are lost.
   * @return hasCheckedToday Flag indicates whether streak has checked today.
   */
  function _getActivationStatus(uint256 currentDayIndex, uint256 lastCheckedDayIndex)
    internal
    pure
    returns (bool isLostStreak, bool hasCheckedToday)
  {
    unchecked {
      isLostStreak = currentDayIndex > lastCheckedDayIndex + 1;
      hasCheckedToday = currentDayIndex == lastCheckedDayIndex;
    }
  }

  /**
   * @dev Return the restorable status of `streak`.
   * @return freezeTimeEnded Flag indicates whether the freeze time are ended.
   */
  function _isFreezeTimeEnded(uint256 lastCheckedDayIndex) internal view returns (bool freezeTimeEnded) {
    unchecked {
      uint256 startOfFreezePeriod = lastCheckedDayIndex * _streakLifeTime + _initialTime;
      freezeTimeEnded = block.timestamp >= startOfFreezePeriod + _freezeTime;
    }
  }

  /// @dev Returns the number of days elapsed from the current timestamp and from the last checker.
  function _calculateElapsedDays(Streak memory streak)
    internal
    view
    returns (uint256 currentDayIndex, uint256 lastCheckedDayIndex)
  {
    currentDayIndex = _getNumberOfIntervals(block.timestamp);
    lastCheckedDayIndex = _getNumberOfIntervals(streak.lastActivated);
  }

  /**
   * @dev Changes to new streak lifetime.
   * @param streakLifeTime The active streak lifetime.
   *
   * Emits the {StreakLifeTimeChanged} event.
   */
  function _setStreakLifeTime(uint256 streakLifeTime) internal {
    if (streakLifeTime == 0) revert ErrInvalidStreakLifeTime();

    _streakLifeTime = streakLifeTime;
    emit StreakLifeTimeChanged(streakLifeTime);
  }

  /// @dev Change to new reset duration.
  function _setResetDuration(uint256 resetDuration) internal {
    if (resetDuration >= _streakLifeTime) revert ErrInvalidResetDuration();
    _resetTime = resetDuration;
    emit ResetDurationUpdated(resetDuration);
  }

  /**
   * @dev Returns the number of time intervals in UTC based on a given timestamp and the reset duration.
   * @param timestamp 	The timestamp to calculate the number of intervals from.
   */
  function _getNumberOfIntervals(uint256 timestamp) internal view returns (uint256 numIntervals) {
    unchecked {
      numIntervals = (timestamp - _initialTime) / _streakLifeTime;
    }
  }

  /// @dev Check if this is a new user
  function _checkNewUser(Streak memory streak) internal pure returns (bool) {
    return streak.lastActivated == 0;
  }

  /// @dev Update the streak info for `to` unsafely.
  function _unsafeCheckin(uint256 streakCount, address to, bool isLostStreak) private returns (uint256 newStreakCount) {
    unchecked {
      newStreakCount = isLostStreak ? 1 : (streakCount + 1);
    }

    Streak storage $ = _usersStreakMap[to];
    $.count = newStreakCount;
    $.lastActivated = block.timestamp;
    if (newStreakCount > $.longestCount) {
      $.longestCount = newStreakCount;
    }

    emit StreakUpdated(to, newStreakCount, block.timestamp, $.longestCount);
  }
}
