pragma solidity ^0.8.23;

import { BaseCheckin } from "./BaseCheckin.sol";

contract CheckinMock is BaseCheckin {
  constructor() BaseCheckin() { }

  function activateStreak(address to) external {
    _activateStreak(to);
  }

  function restoreStreak(address to, uint256 maxAmountIn) external {
    _restoreStreak(to, maxAmountIn);
  }
}
