// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

interface ICheckin {
  struct Streak {
    // the streak count.
    uint256 count;
    // the time of last prayed.
    uint256 lastActivated;
    // The longest streak count.
    uint256 longestCount;
  }

  struct RestorationCost {
    // The lower bounds of lost streak ranges.
    uint32 lowerMilestone;
    // The cost to restore streak pegged in token.
    uint80 costInToken;
  }

  struct CheckerCountInfo {
    uint64 syncedDayIndex;
    uint64 countPerDay;
  }

  /// @dev Emitted when the daily streak of an address of is updated.
  event StreakUpdated(
    address indexed user, uint256 indexed newAmount, uint256 indexed newLastUpdated, uint256 longestStreakCount
  );

  /// @dev Emitted when the daily streak of an address of is updated.
  event StreakRestored(
    address indexed user, uint256 indexed restoredStreakCount, uint256 indexed lastUpdated, uint256 costInPaymentToken
  );

  /// @dev Emitted when the prayer count synced daily.
  event CheckerCountSynced(address indexed prayer, CheckerCountInfo prayerCountInfo);

  /// @dev Emitted when active streak lifetime duration is updated.
  event StreakLifeTimeChanged(uint256 streakLifeTime);

  /// @dev Emitted when the reset steak duration is updated.
  event ResetDurationUpdated(uint256 newResetDuration);
  /// @dev Emitted when the freeze period is updated.
  event FreezePeriodUpdated(uint256 newFreezePeriod);
  /// @dev Emitted when treasury is updated.
  event TreasuryUpdated(address newTreasury);
  /// @dev Emitted when payment token is updated.
  event PaymentTokenUpdated(address newPaymentToken);
  /// @dev Emitted when the restoration cost is updated.
  event RestorationCostUpdated(RestorationCost[] newRestorationCosts);
  /// @dev Emitted when the initial time is updated.
  event InitialTimeUpdated(uint256 time);

  /// @dev Error when streak is still active.
  error ErrAlreadyActivatedStreak();

  /// @dev Error when streak lifetime is invalid e.g. when streak lifetime is 0.
  error ErrInvalidStreakLifeTime();

  /// @dev Error when the reset duration is larger than the streak lifetime.
  error ErrInvalidResetDuration();

  /// @dev Revert when there is no lost streak to restore.
  error ErrNoLostStreak();

  /// @dev Revert when the freeze time period has already ended.
  error ErrFreezeTimeEnded();

  /// @dev Revert when the amount acceptable is insufficient for restoration.
  error ErrInsufficientPayment();

  /// @dev Revert when the caller is not delegated by the address `to`.
  error ErrCallerNotDelegated();

  /// @dev Revert when delegatee attempts start new streak for delegator.
  error ErrStartNewStreakByDelegatee();

  /// @dev Revert when attempting to restore a streak, but the last streak count is 0.
  error ErrZeroLostStreakCount();

  /// @dev Revert when length of input arrays are mismatch.
  error ErrInvalidLength();

  /// @dev Revert when the order of restoration cost array is not ascending based on 'lowerMilestone'.
  error ErrInvalidOrderOfRestorationCosts();

  // @dev Error when streak is still active.
  error ErrNewUserCannotRestore();

  /**
   * @dev Restore streak amount.
   *
   * Requirements:
   * - The caller must be default admin.
   */
  function forceRestoreStreak(address[] calldata addresses, uint256[] calldata streaks) external;

  /// @dev Return the restorable of `user`'s lost streak.
  function isRestorable(address user) external view returns (bool);

  /**
   * @dev Returns streak info including amount and last update timestamp of sender.
   * @return currentStreakCount The current streak count, equal to zero whether streak is lost.
   * @return lastActivated The last pray timestamp.
   * @return longestStreakCount The longest streak count, only affected from upgrading V3.
   * @return lostStreakCount The lost streak count, equal to zero whether streak is still alive.
   *
   */
  function getStreak(address user)
    external
    view
    returns (uint256 currentStreakCount, uint256 lastActivated, uint256 longestStreakCount, uint256 lostStreakCount);

  /**
   * @dev Get the activation status of `user`.
   * @return isLostStreak Indicates whether the streak is lost.
   * @return hasCheckedToday Indicates whether the address `user` has prayed today.
   */
  function getActivationStatus(address user) external view returns (bool isLostStreak, bool hasCheckedToday);

  /// @dev Returns active streak life time.
  function getStreakLifeTime() external view returns (uint256);

  /// @dev Returns the reset duration calculated based on the UTC.
  function getResetDuration() external view returns (uint256);

  /// @dev Returns the freeze period in timestamp for restoration.
  function getFreezePeriod() external view returns (uint256);

  /// @dev Returns the address treasury where receive the restoration fee.
  function getTreasury() external view returns (address);

  /// @dev Returns the address of token of restoration fee.
  function getPaymentToken() external view returns (address);

  /// @dev Returns all the cost in usd according to the lower milestones.
  function getAllRestorationCosts() external view returns (RestorationCost[] memory);

  /// @dev Returns the cost in usd and cost in `paymentToken` according to the the lost streak count.
  function getRestorationCosts(uint256 lostStreakCount) external view returns (uint256 costInPaymentToken);

  /// @dev Returns the Initail Time calculated based on the UTC.
  function getInitialTime() external view returns (uint256);

  /**
   * @dev Set new reset duration for streak.
   * @param newResetDuration The reset duration time.
   *
   * Requirements:
   * - The caller must be default admin.
   * - The reset duration must be less than the streak life time.
   *
   * Emits the {ErrInvalidResetDuration} event.
   */
  function setResetDuration(uint256 newResetDuration) external;

  /**
   * @dev Update freeze period.
   *
   * Requirements:
   * - The caller must has `DEFAULT_ADMIN_ROLE`.
   *
   * Emits {FreezePeriodUpdated} event.
   */
  function setFreezePeriod(uint256 newFreezePeriod) external;

  /**
   * @dev Update address of payment token.
   *
   * Requirements:
   * - The caller must has `DEFAULT_ADMIN_ROLE`.
   *
   * Emits {PaymentTokenUpdated} event.
   */
  function setPaymentToken(address paymentToken) external;

  /**
   * @dev Update address of treasury.
   *
   * Requirements:
   * - The caller must has `DEFAULT_ADMIN_ROLE`.
   *
   * Emits {TreasuryUpdated} event.
   */
  function setTreasury(address treasury) external;

  /**
   * @dev Update the cost for restoration.
   *
   * Requirements:
   * - The caller must has `DEFAULT_ADMIN_ROLE`.
   * - The array `restorationCost` must be sorted as ascending by `lowerMilestone`.
   *
   * Emits {RestorationCostUpdated} event.
   */
  function setRestorationCosts(RestorationCost[] memory restorationCosts) external;
}

